$(document).ready(function(){for(var t=new Date,p=0;p<gameOptions.upgrades.length;p++){var n=gameOptions.upgrades[p];$(gameOptions.buttonList).append('<div class="button-container"><button class data-id="'+p+'" id="'+n.id+'" type="button"><h2>'+n.name+'</h2><p>Price: <span id="button-price">'+n.cost+'</span></p><p>Points per second: <span id="button-pps">'+n.pps+'</span></p><p>Purchased: <span id="button-purchased">'+n.purchased+"</span></p></button></div>")}$(gameOptions.pointAdder).on("click",function(t){t.preventDefault(),gameOptions.points++,$(gameOptions.pointHolder).html(gameOptions.points)}),$(".button-container button").on("click",function(t){t.preventDefault();var p=$(this).attr("data-id");gameOptions.points>=gameOptions.upgrades[p].cost&&(gameOptions.points=gameOptions.points-gameOptions.upgrades[p].cost,gameOptions.pointsPerSecond=gameOptions.pointsPerSecond+gameOptions.upgrades[p].pps,gameOptions.upgrades[p].purchased=gameOptions.upgrades[p].purchased+1,$(this).find("#button-purchased").html(gameOptions.upgrades[p].purchased),gameOptions.updatePurchased(),$("#purchasedTotal").html(gameOptions.purchasedTotal))}),$("#year").html(t.getFullYear())});