var gameOptions={fps:60,pointHolder:"#points",ppsholder:"#pps",points:0,pointAdder:"#post",update:function(){$(this.pointHolder).html(this.points),$(this.ppsholder).html(this.pointsPerSecond)},pointsPerSecond:0,buttonList:".button-list",purchasedTotal:0,updatePurchased:function(){this.purchasedTotal=0;for(var s=0;s<this.upgrades.length;s++){var t=this.upgrades[s];this.purchasedTotal=this.purchasedTotal+t.purchased}},upgrades:[{name:"Schedule post",cost:20,purchased:0,multiplyer:2,pps:1,id:"schedulepost"},{name:"Hire Intern",cost:100,purchased:0,multiplyer:2.5,pps:2,id:"hireintern"},{name:"Sponsored Post",cost:1e3,purchased:0,multiplyer:5,pps:5,id:"sponsoredpost"},{name:"Hire writer",cost:2e4,purchased:0,multiplyer:10,pps:10,id:"hirewriter"},{name:"Rent office",cost:1e5,purchased:0,multiplyer:12,pps:25,id:"rentoffice"}],checkButtons:function(){$(this.buttonList).find("button").each(function(s,t){var e=$(t).attr("data-id");$(t).hasClass("disabled")&&gameOptions.points>=gameOptions.upgrades[e].cost?$(t).removeClass("disabled"):!$(t).hasClass("disabled")&&gameOptions.points<gameOptions.upgrades[e].cost&&$(t).addClass("disabled")})},setOptions:function(s,t){this[s]=t}};